	let playerMaxHp = 100 + Math.min(data.lv * 3, 765) + Math.floor((data.defMedal ?? 0) * 13.4);
	/** ãƒ—ãƒ¬ã‚¤ãƒ¤ãƒ¼ã®è¦‹ãŸç›® */
	let me = color.name;

	let cw = acct(msg.user) + " " + `${data.enemy.msg}`;
